const accordion=document.getElementsByClassName("accordion_header");for(i=0;i<accordion.length;i++)accordion[i].addEventListener("click",function(e){var e=e.target.closest(".accordion_header"),t=e.nextElementSibling;e.classList.toggle("active"),e.classList.contains("active")?t.style.maxHeight=t.scrollHeight+"px":t.style.maxHeight=0});